import{_ as c,c as d,j as e,a as t,G as r,a2 as s,B as i,o as p}from"./chunks/framework.CqvaAnBq.js";const g=JSON.parse('{"title":"JavaScript与Java的关系","description":"","frontmatter":{"lastUpdated":"2024-12-18T00:00:00.000Z"},"headers":[],"relativePath":"JavaScript.md","filePath":"JavaScript.md","lastUpdated":1734480000000}'),n={name:"JavaScript.md"};function v(l,a,J,h,b,u){const o=i("ArticleMetadata");return p(),d("div",null,[a[0]||(a[0]=e("h1",{id:"javascript与java的关系",tabindex:"-1"},[t("JavaScript与Java的关系 "),e("a",{class:"header-anchor",href:"#javascript与java的关系","aria-label":'Permalink to "JavaScript与Java的关系"'},"​")],-1)),r(o),a[1]||(a[1]=s('<div class="caution custom-block github-alert"><p class="custom-block-title">CAUTION</p><p><strong>前言</strong><br> 实在有太多人混淆<code>JavaScript</code>和<code>Java</code>了<br> 甚至在各种网络平台也可以见到，<code>JavaScript</code>语法不像<code>Java</code>像什么的言论，于是出一篇科普吐槽文章</p></div><h2 id="先说结论" tabindex="-1">先说结论 <a class="header-anchor" href="#先说结论" aria-label="Permalink to &quot;先说结论&quot;">​</a></h2><p><code>JavaScript</code>和<code>Java</code>的关系<br><strong>就是雷峰塔与雷锋的关系</strong></p><h3 id="也就是没有关系" tabindex="-1">也就是没有关系 <a class="header-anchor" href="#也就是没有关系" aria-label="Permalink to &quot;也就是没有关系&quot;">​</a></h3><p>下面我们来聊聊什么是<code>JavaScript</code>，什么是<code>Java</code>，为什么他们的名字都有<code>Java</code></p><h2 id="javascript是什么" tabindex="-1">JavaScript是什么 <a class="header-anchor" href="#javascript是什么" aria-label="Permalink to &quot;JavaScript是什么&quot;">​</a></h2><p><code>JavaScript</code>是一种动态的编程脚本语言，广泛应用于网页开发中，主要用于增强网页的交互性和动态性。作为Web开发的核心技术之一</p><p>1995年，<code>JavaScript</code>由Netscape的工程师Brendan Eich发明，最初命名为<code>Mocha</code>，后改为<code>LiveScript</code>，最终定名为<code>JavaScript</code></p><p><strong>实际上只是叫这个名字而已</strong></p><h3 id="特点是" tabindex="-1">特点是 <a class="header-anchor" href="#特点是" aria-label="Permalink to &quot;特点是&quot;">​</a></h3><p><code>前端语言</code><br><code>动态类型(弱类型)语言</code><br><code>事件驱动</code><br><code>基于HTML和CSS</code></p><h2 id="java是什么" tabindex="-1">Java是什么 <a class="header-anchor" href="#java是什么" aria-label="Permalink to &quot;Java是什么&quot;">​</a></h2><p><code>Java</code>是一种<code>OOP</code>（面向对象的编程语言）、基于类的、具有<code>VM（虚拟机）</code>平台的多平台编程语言。<code>OOP</code> 是一种基于包含代码和数据的对象概念的编程范式</p><p>1995年Sun公司将Oak更名为<code>Java</code>。5月23日，Sun公司在Sun world会议上正式发布<code>Java</code>和<code>HotJava</code>浏览器</p><h3 id="特点是-1" tabindex="-1">特点是 <a class="header-anchor" href="#特点是-1" aria-label="Permalink to &quot;特点是&quot;">​</a></h3><p><code>后端语言</code><br><code>面向对象</code><br><code>基于JDK</code></p><h2 id="javascript和java之间的区别" tabindex="-1">JavaScript和Java之间的区别 <a class="header-anchor" href="#javascript和java之间的区别" aria-label="Permalink to &quot;JavaScript和Java之间的区别&quot;">​</a></h2><p><strong>1.</strong><code>Javascript</code>与<code>Java</code>是由不同的公司开发的不同产品。<code>Javascript</code>是<code>Netscape</code>公司的产品，其目的是为了扩展<code>Netscape Navigator</code>功能,而开发的一种可以嵌入<code>Web</code>页面中的基于对象和事件驱动的解释性语言；而<code>Java</code>是<code>SUN Microsystems</code>公司推出的新一代面向对象的程序设计语言，特别适合于<code>Internet</code>应用程序开发。</p><p><strong>2.</strong><code>Javascript</code>是基于对象的，它是一种脚本语言，是一种基于对象和事件驱动的编程语言，因而它本身提供了非常丰富的内部对象供设计人员使用。而<code>Java</code>是面向对象的，即<code>Java</code>是一种真正的面向对象的语言，即使是开发简单的程序也必须设计对象。</p><p><strong>3.</strong><code>Javascript</code>与<code>Java</code>嵌入方式不一样。在<code>HTML</code>文档中，两种编程语言的标识不同，<code>Javascript</code>使用<code>&lt;script&gt;&lt;/script&gt;</code>来标识，而<code>Java</code>使用<code>Applet</code>来标识。</p><p><strong>4.</strong><code>Javascript</code>与<code>Java</code>在浏览器中所执行的方式不一样。<code>Javascript</code>是一种解释性编程语言，其源代码在发往客户端执行之前不需经过编译，而是将文本格式的字符代码发送给客户，即<code>Javascript</code>语句本身随<code>Web</code>页面一起下载下来，由浏览器解释执行。而<code>Java</code>的源代码在传递到客户端执行之前，必须经过编译，因而客户端上必须具有相应平台上的仿真器或解释器，它可以通过编译器或解释器实现独立于某个特定的平台编译代码。</p><p><strong>5.</strong><code>Javascript</code>与<code>Java</code>代码格式不一样。<code>Javascript</code>的代码是一种谋咀址格式，可以直接嵌入HTML文档中，并且可动态装载，编写<code>HTML</code>文档就像编辑文本文件一样方便，其独立文件的格式为*.js。<code>Java</code>是一种与<code>HTML</code>无关的格式，必须通过像<code>HTML</code>中引用外媒体那么进行装载，其代码以字节代码的形式保存在独立的文档中，其独立文件的格式为*.class。</p><h2 id="最后" tabindex="-1">最后 <a class="header-anchor" href="#最后" aria-label="Permalink to &quot;最后&quot;">​</a></h2><p><strong><code>Javascript</code>是<code>Netscape</code>公司的产品，<code>Java</code>是<code>SUN Microsystems</code>公司的产品。</strong><br><strong>无论是在商业还是语法和应用层面上，他们都没什么关系</strong></p>',24))])}const m=c(n,[["render",v]]);export{g as __pageData,m as default};
